'FARE_CLASS';


// CHANGE # 4: Replace this with your CONNECT statement
LIB CONNECT TO 'QLIK_HANA (qtsel_icd)';




// CHANGE #2:  Alter this with a leading 'WHERE <condition>' if you want your SQL statement (below)
//             to have a non-changing WHERE clause in addition to the clauses that will be inserted
//             by the shopping cart app (it is fine to leave it as is).
SET WHERE_PART = '';

// CHANGE 3: Update the list of field names here to reflect each of the field names variables you have on the
//           left hand side (assignment target) of the first SET statement of the SET statement pairs in change
//           1 above.
FOR EACH fldname IN 'ORIGIN', 'DEST', 'YEAR', 'QUARTER', 'ORIGIN_STATE_ABR', 'DEST_STATE_ABR', 'TICKET_CARRIER', 'FARE_CLASS'
  LET vallist = $($(fldname));
  WHEN (IsNull(vallist)) LET vallist = '';
  IF len(vallist) > 0 THEN
    CALL ExtendWhere('$(fldname)','vallist');
  ENDIF
NEXT fldname


TRACE Generated WHERE clause: ;
TRACE $(WHERE_PART);

// CHANGE # 5: Replace everything EXCEPT the $(WHERE_PART) portion of the SQL SELECT statement with 
//             query you wish to send to the database.  Note that you can have more than one of these
//             dynamiclly alterered SELECT statemnents by replicating the sections from CHANGE #2 thru this
//             change #5 and customize which WHERE clauses will be inserted by altering the list of fields
//             in the FOR EACH statement in Change #3.
LOAD  "FARE_CLASS" as "Fare Class",
      "ORIGIN_STATE_ABR" as "Origin State",
      "DEST_STATE_ABR" as "Destination State",
      "QUARTER" as "Quarter",
      "ORIGIN" as "Origin Code",
      "DEST" as "Destination Code",
      "TICKET_CARRIER" as "Ticket Carrier Code",
      "YEAR" as "Year",
      "FLIGHT_COUNT",
      "PASSENGERS",
      "DISTANCE",
      "MKT_ID";
SQL SELECT 	"MKT_ID",
			"YEAR",
			"QUARTER",
            "ORIGIN",
            "ORIGIN_STATE_ABR",
            "DEST",
            "DEST_STATE_ABR",
            "TICKET_CARRIER",
            "FARE_CLASS",
            "PASSENGERS",
            "DISTANCE",
            1 AS "FLIGHT_COUNT"
FROM "SAPH7T"."/QT/AIRPORT_FACT"
//$(WHERE_PART);
WHERE "ORIGIN" = 'MCO' and FARE_CLASS = 'G' and TICKET_CARRIER ='VX';

Airlines:
LOAD TICKET_CARRIER as "Ticket Carrier Code",
    "Description" as Airline
WHERE Exists("Ticket Carrier Code","TICKET_CARRIER");
SQL SELECT TICKET_CARRIER,
    "Description"
FROM "SAPH7T"."/QT/CARRIERS";

OriginAirports:
LOAD "Code" as "Origin Code",
    "Description" as "Origin Name"
WHERE Exists("Origin Code","Code");
SQL SELECT "Code",
    "Description"
FROM "SAPH7T"."/QT/AIRPORT_CODE";

DestAirports:
LOAD "Code" as "Destination Code",
    "Description" as "Destination Name"
WHERE Exists("Destination Code","Code");
SQL SELECT "Code",
    "Description"
FROM "SAPH7T"."/QT/AIRPORT_CODE";

Fares:
LOAD FARE_CLASS as "Fare Class",
    "Fare_Class_Description" as "Fare Class Name"
WHERE Exists("Fare Class","FARE_CLASS");
SQL SELECT "FARE_CLASS",
    "Fare_Class_Description"
FROM  "SAPH7T"."/QT/FARE_CLASS";



